import sys
# 1-2年生の漢字
allowed = set\('一右雨円王音下火花貝学気九休玉金空月犬見五口校左三山子四糸字耳七車手十出女小上森人水正生青夕石赤千川先早草足村大男竹中虫町天田土二日入年白八百文木本名目立力林六引羽雲園遠何科夏家歌画回会海絵外角楽活間丸岩顔汽記帰弓牛魚京強教近兄形計元言原戸古午後語工公広交光考行高黄合谷国黒今才細作算止市矢姉思紙寺自時室社弱首秋週春書少場色食心新親図数西声星晴切雪船線前組走多太体台地池知茶昼長鳥朝直通弟店点電刀冬当東答頭同道読内南肉馬売買麦半番父風分聞米歩母方北毎妹万明鳴毛門夜野友用曜来里理話'\)

errors = []
for line in sys.stdin:
    parts = line.strip\(\).split\('|'\)
    if len\(parts\) < 5:
        continue
    qid, pre, kanji, reading, post = parts[0], parts[1], parts[2], parts[3], parts[4]
    # Check pre_text and post_text for non-allowed kanji
    for text, label in [\(pre, 'pre_text'\), \(post, 'post_text'\)]:
        for ch in text:
            if '\\u4e00' <= ch <= '\\u9fff' and ch not in allowed:
                errors.append\(f'Q{qid}: {label} に不正な漢字「{ch}」 \({pre}{kanji}[{reading}]{post}\)'\)
if errors:
    for e in errors:
        print\(e\)
else:
    print\('OK: すべての問題文は1〜2年生の漢字のみを使用しています。'\)
")

